mapper.select:

    select

    from (


    select

    from t_knowledge_tree
    where 1=1

        and parent_id = #{parentId}


        and status in

            #{item}



        and tree_type in

            #{item}


    and organ_id = #{organId}
    and type = 1
    and id in (select knowledge_tree_id from t_engine_knowledge_tree_rel td where engine_id = #{engineId})
    union

select

from t_knowledge_tree
where 1=1

    and parent_id = #{parentId}


    and status in

        #{item}



    and tree_type in

        #{item}



    and type = 0


    and organ_id = #{organId}
    and type = 1

        order by tree_type,id



    and engine_id = #{engineId}
    and type = 2
    ) y

        order by y.tree_type,y.id

select

from t_knowledge_tree
where 1=1

    and parent_id = #{parentId}


    and status in

        #{item}



    and tree_type in

        #{item}


and organ_id = #{organId}
and type = #{type}

    order by tree_type,id


    select

    from (


    select

    from t_knowledge_tree k
    where 1=1

        and k.parent_id = #{parentId}


        and k.status in

            #{item}



        and k.tree_type in

            #{item}


    and k.organ_id = #{organId}
    and k.type = 1
    and k.id in (select knowledge_tree_id from t_engine_knowledge_tree_rel td where td.engine_id = #{engineId})
    and (select count(r.parent_id) from t_rule r where r.parent_id = k.id and r.rule_type=1) > 0
    union

select

from t_knowledge_tree k
where 1=1

    and k.parent_id = #{parentId}


    and k.status in

        #{item}



    and k.tree_type in

        #{item}



    and k.type = 0


    and k.organ_id = #{organId}
    and k.type = 1

        order by k.tree_type,k.id



    and k.engine_id = #{engineId}
    and k.type = 2
    and (select count(r.parent_id) from t_rule r where r.parent_id = k.id  and r.rule_type=1) > 0
    ) y

        order by y.tree_type,y.id


    select

    from (


