select
id
from t_knowledge_tree


        and `name` = #{name}


        and parent_id = #{parentId}


        and status = #{status}


        and tree_type = #{treeType}


        and user_id = #{userId}


        and organ_id = #{organId}

select id ,`name`
from t_knowledge_tree
